package com.advanto1.practise;

public class DatatypeDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

				int a = 10;
				int b=20;
				int add=30;
				System.out.println(add);
				
				char ch='b';//only represent single character
				System.out.println(ch);
				
				boolean c=true;// check true or flase
				System.out.println(c);
				
				long addd=123l;//int type 
				System.out.println(addd);
				
				double bb =12.1;//decimal type
				System.out.println(bb);
				
				float ff=12.5f;//decimale type
				System.out.println(ff);
				
				String ss="akshay";//text type
				System.out.println(ss);
				
			
	}

}
