package com.startprogram.practise;

public class Sqaurestar {
	
	
	public void s()
	{
		for(int i = 1;i<=4;i++)//row
		{
			
			for(int j=1; j<=4; j++)//column
			{
				System.out.print(" *");
				
			}
			System.out.println();
		}
		
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Sqaurestar Star = new Sqaurestar();
		
	      Star.s();
		
			
			
		

	}

}
