package com.startprogram.practise;

public class pyramid {

	
	public void p()
	{
		
		for(int i=1;i<=4;i++)
		{
		
			for(int k=4;k==4;k--)
			{
				
				System.out.print("*");
			}
			System.out.println();
		}
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		pyramid ps= new pyramid();
		
		ps.p();
				
		
		

	}

}
